import React, { useState } from 'react' // 1. Import useState
import styled from 'styled-components'
import avatar from '../../img/avatar.png'
import { menuItems } from '../../utils/menuitems'

// 2. Assuming you are using an icon library like react-icons
import { BiLogOut } from 'react-icons/bi'; // Example signout icon

// 3. Define the signout variable (if not an imported component)
const signout = <BiLogOut />; // Placeholder for the signout icon

function Navigation() {
    // 1. Define the active state for the menu items
    const [active, setActive] = useState(1); // Set a default active item, e.g., '1'

    return (
        <NavStyled>
            <div className="user-con">
                <img src={avatar} alt="user" />
                <h2>Kamran</h2>
                <p>Your Money</p>
            </div>
            <ul className="menu-items">
                {menuItems.map((item) => {
                    return <li
                        key={item.id}
                        onClick={() => setActive(item.id)}
                        className={active === item.id ? 'active' : ''}
                    >
                        {item.icon}
                        <span>{item.title}</span>
                    </li>
                })}
            </ul>
            <div className="bottom-nav">
                <li>
                    {signout} Sign Out
                </li>
            </div>
        </NavStyled>
    )
}
// You'll still need to add your actual CSS styling inside the backticks below
const NavStyled = styled.nav`
    /* Add your styles here */
`;
export default Navigation
